def convert_hex_string_to_rsa_key(hex_string):
    # Remove the "\x" prefixes and split by each two characters (as hex codes are two characters long)
    hex_codes = [hex_string[i : i + 2] for i in range(0, len(hex_string), 2)]

    # Convert each hex code to its character equivalent
    chars = [chr(int(code, 16)) for code in hex_codes]

    return "".join(chars)


hex_string = ""  # Replace with your hex string (excluding the "\x")
converted_key = convert_hex_string_to_rsa_key(hex_string)
print(converted_key)
